examples.value:
curl -H "Content-Type: application/json" \
  -H "Authorization: Bearer $TOKEN" \
  -X POST -d '{
  "kernel": "linode/latest-64bit",
  "label": "My config",
  "devices": {
    "sda": {
      "disk_id": 123456,
      "volume_id": 120394
    },
    "sdb": {
      "disk_id": 123456,
      "volume_id": 120394
    },
    "sdc": null,
    "sdd": null,
    "sde": null,
    "sdf": null,
    "sdg": null,
    "sdh": null
  },
  "comments": "This is my config.",
  "helpers": {
    "updatedb_disabled": true,
    "distro": true,
    "modules_dep": true,
    "network": true
  }
}' \
  https://api.linode.com/v4/linode/instances/$linode_id/configs

curl -H "Content-Type: application/json" \
    -H "Authorization: Bearer $TOKEN" \
    -X PUT -d '{
      "label": "New Disk Label"
    }' \
    https://api.linode.com/v4/linode/instances/$linode_id/disks/$disk_id

curl -H "Content-Type: application/json" \
    -H "Authorization: Bearer $TOKEN" \
    -X POST -d '{
        "description": "More details about the ticket",
    }' \
    https://api.linode.com/v4/support/tickets/$ticket_id/replies
